import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

function App() {
  const [userContext, setUserContext] = useState(null);
  const [projects, setProjects] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [sentimentInput, setSentimentInput] = useState('');
  const [sentimentResult, setSentimentResult] = useState(null);

  useEffect(() => {
    fetchProjects();
  }, []);

  useEffect(() => {
    if (userContext && userContext.theme) {
      applyTheme(userContext.theme);
    }
  }, [userContext]);

  const applyTheme = (theme) => {
    const root = document.documentElement;
    Object.entries(theme).forEach(([key, value]) => {
      root.style.setProperty(key, value);
    });
  };

  const fetchProjects = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/projects`);
      setProjects(response.data);
    } catch (err) {
      console.error('Error fetching projects:', err);
    }
  };

  const getLocationAndContext = async () => {
    setIsLoading(true);
    setError(null);

    if (!navigator.geolocation) {
      setError("Geolocation is not supported by your browser.");
      setIsLoading(false);
      return;
    }

    navigator.geolocation.getCurrentPosition(
      async (position) => {
        const { latitude, longitude } = position.coords;
        try {
          const response = await axios.post(`${API_BASE_URL}/get-context/`, {
            latitude,
            longitude
          });
          setUserContext(response.data);
        } catch (err) {
          setError('Failed to get context from server.');
          console.error(err);
        } finally {
          setIsLoading(false);
        }
      },
      (err) => {
        setError('Please allow location access to experience the full feature.');
        setIsLoading(false);
      }
    );
  };

  const analyzeSentiment = async () => {
    if (!sentimentInput.trim()) return;

    try {
      const response = await axios.post(`${API_BASE_URL}/analyze-sentiment/`, {
        text: sentimentInput
      }, {
        headers: {
          'Content-Type': 'application/json'
        }
      });
      setSentimentResult(response.data);
    } catch (err) {
      console.error('Error analyzing sentiment:', err);
    }
  };

  return (
    <div className="App">
      <header className="hero-section">
        <div className="container">
          <h1>Hi, I'm [Your Name]</h1>
          <p className="tagline">AI & Machine Learning Engineer</p>
          <p className="bio">
            Building intelligent systems that learn and adapt. Passionate about 
            deep learning, computer vision, and creating AI that makes a difference.
          </p>

          <button 
            onClick={getLocationAndContext} 
            disabled={isLoading}
            className="cta-button"
          >
            {isLoading ? 'Analyzing Environment...' : 'Adapt to My Location'}
          </button>

          {error && <p className="error-message">{error}</p>}

          {userContext && (
            <div className="context-display">
              <div className="ai-message">
                <strong>AI Assistant:</strong> "{userContext.ai_message}"
              </div>
              <div className="weather-info">
                <span>Temperature: {userContext.weather_data.temperature}°C</span>
                <span>•</span>
                <span>Context: {userContext.context}</span>
              </div>
            </div>
          )}
        </div>
      </header>

      <section className="projects-section">
        <div className="container">
          <h2>Featured Projects</h2>
          <div className="projects-grid">
            {projects.map(project => (
              <div key={project.id} className="project-card">
                <h3>{project.title}</h3>
                <p>{project.description}</p>
                <div className="tags">
                  {project.tags.map(tag => (
                    <span key={tag} className="tag">{tag}</span>
                  ))}
                </div>
                <div className="project-links">
                  <a href={project.github_url} target="_blank" rel="noopener noreferrer">
                    GitHub
                  </a>
                  {project.live_url && (
                    <a href={project.live_url} target="_blank" rel="noopener noreferrer">
                      Live Demo
                    </a>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      <section className="ai-demo-section">
        <div className="container">
          <h2>AI Sentiment Analysis Demo</h2>
          <div className="sentiment-demo">
            <textarea
              value={sentimentInput}
              onChange={(e) => setSentimentInput(e.target.value)}
              placeholder="Enter text to analyze sentiment..."
              rows="3"
            />
            <button onClick={analyzeSentiment} className="analyze-button">
              Analyze Sentiment
            </button>
            {sentimentResult && (
              <div className="sentiment-result">
                <p>Sentiment: <strong>{sentimentResult.sentiment}</strong></p>
                <p>Confidence: {(sentimentResult.confidence * 100).toFixed(1)}%</p>
              </div>
            )}
          </div>
        </div>
      </section>

      <footer className="footer">
        <div className="container">
          <p>Built with React, FastAPI, and Hugging Face Transformers</p>
          <p>© 2024 [Your Name]. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
}

export default App;
