import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

function App() {
  const [userContext, setUserContext] = useState(null);
  const [projects, setProjects] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [sentimentInput, setSentimentInput] = useState('');
  const [sentimentResult, setSentimentResult] = useState(null);
  const [backendStatus, setBackendStatus] = useState('checking');

  // Check backend connection on component mount
  useEffect(() => {
    checkBackendConnection();
    fetchProjects();
  }, []);

  const checkBackendConnection = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/`);
      if (response.data.message) {
        setBackendStatus('connected');
      }
    } catch (err) {
      setBackendStatus('disconnected');
      setError('Backend server is not running. Please start the backend server.');
    }
  };

  const applyTheme = (theme) => {
    const root = document.documentElement;
    Object.entries(theme).forEach(([key, value]) => {
      root.style.setProperty(key, value);
    });
  };

  const fetchProjects = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/projects`);
      setProjects(response.data);
    } catch (err) {
      console.error('Error fetching projects:', err);
    }
  };

  const getLocationAndContext = async () => {
    if (backendStatus === 'disconnected') {
      setError('Backend server is not running. Please start the backend server first.');
      return;
    }

    setIsLoading(true);
    setError(null);

    if (!navigator.geolocation) {
      setError("Geolocation is not supported by your browser.");
      setIsLoading(false);
      return;
    }

    navigator.geolocation.getCurrentPosition(
      async (position) => {
        const { latitude, longitude } = position.coords;
        try {
          const response = await axios.post(`${API_BASE_URL}/get-context/`, {
            latitude,
            longitude
          });
          setUserContext(response.data);
          setError(null);
        } catch (err) {
          setError('Failed to get context from server. Make sure backend is running on port 8000.');
          console.error('Server error:', err);
        } finally {
          setIsLoading(false);
        }
      },
      (err) => {
        setError('Please allow location access to experience the full feature.');
        setIsLoading(false);
      }
    );
  };

  const analyzeSentiment = async () => {
    if (!sentimentInput.trim()) return;

    try {
      const response = await axios.post(`${API_BASE_URL}/analyze-sentiment/`, {
        text: sentimentInput
      });
      setSentimentResult(response.data);
    } catch (err) {
      setError('Sentiment analysis service unavailable. Check backend connection.');
      console.error('Sentiment analysis error:', err);
    }
  };

  return (
    <div className="App">
      {/* Status Indicator */}
      <div className={`status-indicator ${backendStatus}`}>
        Backend: {backendStatus === 'connected' ? '✅ Connected' : '❌ Disconnected'}
      </div>

      <header className="hero-section">
        <div className="container">
          <h1>Hi, I'm Yashwanth</h1>
          <p className="tagline">AI & Machine Learning Engineer</p>
          <p className="bio">
            Building intelligent systems that learn and adapt. Passionate about 
            deep learning, computer vision, and creating AI that makes a difference.
          </p>

          <button 
            onClick={getLocationAndContext} 
            disabled={isLoading || backendStatus === 'disconnected'}
            className="cta-button"
          >
            {isLoading ? '🌍 Analyzing Environment...' : '🎯 Adapt to My Location'}
          </button>

          {error && (
            <div className="error-message">
              ⚠️ {error}
              {error.includes('backend') && (
                <div className="solution-hint">
                  Run: <code>cd backend && uvicorn main:app --reload</code>
                </div>
              )}
            </div>
          )}

          {userContext && (
            <div className="context-display">
              <div className="ai-message">
                <strong>🤖 AI Assistant:</strong> "{userContext.ai_message}"
              </div>
              <div className="weather-info">
                <span>🌡️ {userContext.weather_data.temperature}°C</span>
                <span>•</span>
                <span>🌤️ {userContext.context}</span>
              </div>
              <div className="featured-project">
                <h3>⭐ Recommended Project:</h3>
                <p>{userContext.featured_project.title}</p>
              </div>
            </div>
          )}
        </div>
      </header>

      <section className="projects-section">
        <div className="container">
          <h2>🚀 Featured Projects</h2>
          <div className="projects-grid">
            {projects.map(project => (
              <div key={project.id} className="project-card">
                <h3>{project.title}</h3>
                <p>{project.description}</p>
                <div className="tags">
                  {project.tags.map(tag => (
                    <span key={tag} className="tag">{tag}</span>
                  ))}
                </div>
                <div className="project-links">
                  <a href={project.github_url} target="_blank" rel="noopener noreferrer">
                    📁 GitHub
                  </a>
                  {project.live_url && (
                    <a href={project.live_url} target="_blank" rel="noopener noreferrer">
                      🌐 Live Demo
                    </a>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      <section className="ai-demo-section">
        <div className="container">
          <h2>🧠 AI Sentiment Analysis Demo</h2>
          <div className="sentiment-demo">
            <textarea
              value={sentimentInput}
              onChange={(e) => setSentimentInput(e.target.value)}
              placeholder="Enter text to analyze sentiment (e.g., 'I love this amazing project!')..."
              rows="3"
            />
            <button onClick={analyzeSentiment} className="analyze-button">
              Analyze Sentiment
            </button>
            {sentimentResult && (
              <div className="sentiment-result">
                <p>Sentiment: <strong>
                  {sentimentResult.sentiment === 'positive' ? '✅ Positive' : 
                   sentimentResult.sentiment === 'negative' ? '❌ Negative' : '⚪ Neutral'}
                </strong></p>
                <p>Confidence: {(sentimentResult.confidence * 100).toFixed(1)}%</p>
              </div>
            )}
          </div>
        </div>
      </section>

      <footer className="footer">
        <div className="container">
          <p>⚡ Built with React, FastAPI, and Hugging Face Transformers</p>
          <p>© 2024 Yashwanth. All rights reserved.</p>
          <div className="social-links">
            <a href="https://github.com/yourusername" target="_blank" rel="noopener noreferrer">GitHub</a>
            <a href="https://linkedin.com/in/yourusername" target="_blank" rel="noopener noreferrer">LinkedIn</a>
            <a href="mailto:your.email@example.com">Email</a>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default App;
