import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './App.css';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

function App() {
  const [userContext, setUserContext] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [backendStatus, setBackendStatus] = useState('checking');
  const [activeSection, setActiveSection] = useState('home');
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const sections = useRef({
    home: null,
    about: null,
    experience: null,
    projects: null,
    skills: null,
    education: null,
    certifications: null,
    contact: null
  });

  // Resume data from your LaTeX document
  const resumeData = {
    name: "Yashwanth R",
    title: "Artificial Intelligence & Machine Learning Student",
    contact: {
      email: "yashwanth150204@gmail.com",
      phone: "9345570940",
      linkedin: "linkedin.com/in/yashwanth1524"
    },
    experience: [
      {
        role: "Research Intern - AI for Music Digitization",
        period: "Jul 2024 - Oct 2024",
        company: "University of Malaya Institute for Advanced Studies, Malaysia",
        achievements: [
          "Developed AI-based Optical Music Recognition (OMR) system achieving 92% accuracy",
          "Implemented hybrid denoising algorithm reducing noise by 40% in aged documents",
          "Designed segmentation pipeline improving staff line detection accuracy by 35%",
          "Integrated ABC notation converter for standardized digital output",
          "Collaborated with musicologists to validate results on Primus dataset"
        ]
      }
    ],
    projects: [
      {
        name: "Musical Sheet Digitization Pipeline",
        technologies: "Python, TensorFlow, OpenCV",
        description: "Built end-to-end system for denoising, recognizing, and converting musical notation",
        achievements: [
          "Achieved 89% symbol recognition accuracy on handwritten sheets",
          "Optimized segmentation algorithm reduced processing time by 30% per sheet"
        ]
      },
      {
        name: "E-commerce Application with Servlets",
        technologies: "Java, Servlets, MySQL",
        description: "Engineered a Java-based E-commerce application using Servlets",
        achievements: [
          "25% reduction in order processing time",
          "40% increase in user engagement"
        ]
      },
      {
        name: "Blog Website with ReactJS and Firebase",
        technologies: "React, Firebase, HTML/CSS",
        description: "Full-stack blog application enabling creation of 500+ blog posts",
        achievements: [
          "Enhanced content management efficiency by 40%",
          "Improved user engagement with real-time updates"
        ]
      }
    ],
    education: [
      {
        degree: "M.Sc. Artificial Intelligence and Machine Learning",
        period: "Nov 2021 - Jul 2026",
        institution: "Coimbatore Institute of Technology",
        details: "CGPA: 7.89"
      },
      {
        degree: "Higher Secondary (XII)",
        period: "2020 - 2021",
        institution: "Sri Vijay Vidyalaya Matric Hr. Sec. School",
        details: "92%"
      },
      {
        degree: "Secondary (X)",
        period: "2018 - 2019",
        institution: "Sri Vijay Vidyalaya Matric Hr. Sec. School",
        details: "94.8%"
      }
    ],
    skills: {
      "Machine Learning": ["TensorFlow", "PyTorch", "Scikit-learn", "OpenCV", "CNN", "RNN"],
      "Programming": ["Python", "Java"],
      "Web Development": ["React", "Firebase", "HTML", "CSS", "REST APIs", "Servlets"],
      "Tools": ["Git", "Docker", "Jupyter"]
    },
    certifications: [
      {
        name: "CSG - Data Journalism with Python",
        issuer: "CSG Tech"
      },
      {
        name: "Google DSC - Summer Hackathon",
        issuer: "Machine Learning Track"
      },
      {
        name: "KRIYA - App Development Workshop",
        issuer: "PSG Tech"
      }
    ]
  };

  useEffect(() => {
    checkBackendConnection();
    setupIntersectionObserver();
    setupAnimations();
  }, []);

  const setupIntersectionObserver = () => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            setActiveSection(entry.target.id);
          }
        });
      },
      { threshold: 0.5 }
    );

    Object.values(sections.current).forEach(section => {
      if (section) observer.observe(section);
    });
  };

  const setupAnimations = () => {
    // This will be handled by CSS animations
  };

  const checkBackendConnection = async () => {
    try {
      await axios.get(`${API_BASE_URL}/`);
      setBackendStatus('connected');
    } catch (err) {
      setBackendStatus('disconnected');
    }
  };

  const scrollToSection = (sectionId) => {
    sections.current[sectionId]?.scrollIntoView({ behavior: 'smooth' });
    setIsMenuOpen(false);
  };

  const getLocationAndContext = async () => {
    if (backendStatus === 'disconnected') {
      setError('Backend server is not running. Please start the backend server first.');
      return;
    }

    setIsLoading(true);
    setError(null);

    if (!navigator.geolocation) {
      setError("Geolocation is not supported by your browser.");
      setIsLoading(false);
      return;
    }

    navigator.geolocation.getCurrentPosition(
      async (position) => {
        const { latitude, longitude } = position.coords;
        try {
          const response = await axios.post(`${API_BASE_URL}/get-context/`, {
            latitude,
            longitude
          });
          setUserContext(response.data);
          setError(null);
        } catch (err) {
          setError('Failed to get context from server.');
        } finally {
          setIsLoading(false);
        }
      },
      (err) => {
        setError('Please allow location access to experience the full feature.');
        setIsLoading(false);
      }
    );
  };

  return (
    <div className="App">
      {/* Navigation */}
      <nav className="navbar">
        <div className="nav-container">
          <div className="nav-logo">
            <span>Y</span>ashwanth
          </div>
          <div className={`nav-menu ${isMenuOpen ? 'active' : ''}`}>
            {['home', 'about', 'experience', 'projects', 'skills', 'education', 'certifications', 'contact'].map((item) => (
              <button
                key={item}
                className={`nav-link ${activeSection === item ? 'active' : ''}`}
                onClick={() => scrollToSection(item)}
              >
                {item.charAt(0).toUpperCase() + item.slice(1)}
              </button>
            ))}
          </div>
          <button className="nav-toggle" onClick={() => setIsMenuOpen(!isMenuOpen)}>
            <span></span>
            <span></span>
            <span></span>
          </button>
        </div>
      </nav>

      {/* Hero Section */}
      <section id="home" className="hero" ref={el => sections.current.home = el}>
        <div className="hero-background">
          <div className="particles"></div>
          <div className="floating-shapes">
            <div className="shape shape-1"></div>
            <div className="shape shape-2"></div>
            <div className="shape shape-3"></div>
          </div>
        </div>
        <div className="hero-content">
          <div className="hero-text">
            <h1 className="hero-title">
              Hi, I'm <span className="text-gradient">{resumeData.name}</span>
            </h1>
            <h2 className="hero-subtitle">{resumeData.title}</h2>
            <p className="hero-description">
              Passionate about building intelligent systems that learn and adapt. 
              Specializing in deep learning, computer vision, and creating AI that makes a difference.
            </p>
            <div className="hero-buttons">
              <button className="btn-primary" onClick={() => scrollToSection('projects')}>
                View My Work
              </button>
              <button className="btn-secondary" onClick={() => scrollToSection('contact')}>
                Get In Touch
              </button>
              <button 
                className="btn-tertiary"
                onClick={getLocationAndContext}
                disabled={isLoading}
              >
                {isLoading ? 'üåç Analyzing Environment...' : 'üéØ Adapt UI'}
              </button>
            </div>
          </div>
          <div className="hero-visual">
            <div className="profile-orb">
              <div className="orb-inner">
                <div className="orb-content">
                  <div className="initials">YR</div>
                  <div className="orb-ring"></div>
                  <div className="orb-particle"></div>
                  <div className="orb-particle"></div>
                  <div className="orb-particle"></div>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="scroll-indicator">
          <div className="scroll-arrow"></div>
        </div>
      </section>

      {/* About Section */}
      <section id="about" className="section" ref={el => sections.current.about = el}>
        <div className="container">
          <h2 className="section-title">About Me</h2>
          <div className="about-grid">
            <div className="about-content">
              <p>
                I'm a passionate AI and Machine Learning student with expertise in developing 
                intelligent systems. My work focuses on deep learning, computer vision, and 
                creating practical AI solutions that solve real-world problems.
              </p>
              <p>
                With experience in research and development, I've worked on projects ranging from 
                music digitization to e-commerce applications. I thrive on challenges and enjoy 
                pushing the boundaries of what's possible with AI.
              </p>
              <div className="about-stats">
                <div className="stat">
                  <h3>5+</h3>
                  <p>Projects Completed</p>
                </div>
                <div className="stat">
                  <h3>3+</h3>
                  <p>Years Learning</p>
                </div>
                <div className="stat">
                  <h3>15+</h3>
                  <p>Technologies Mastered</p>
                </div>
              </div>
            </div>
            <div className="about-image">
              <div className="floating-card">
                <h3>Quick Info</h3>
                <div className="info-item">
                  <strong>Email:</strong> {resumeData.contact.email}
                </div>
                <div className="info-item">
                  <strong>Phone:</strong> {resumeData.contact.phone}
                </div>
                <div className="info-item">
                  <strong>LinkedIn:</strong> {resumeData.contact.linkedin}
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Experience Section */}
      <section id="experience" className="section" ref={el => sections.current.experience = el}>
        <div className="container">
          <h2 className="section-title">Professional Experience</h2>
          <div className="timeline">
            {resumeData.experience.map((exp, index) => (
              <div key={index} className="timeline-item">
                <div className="timeline-marker"></div>
                <div className="timeline-content">
                  <h3>{exp.role}</h3>
                  <h4>{exp.company}</h4>
                  <span className="timeline-period">{exp.period}</span>
                  <ul className="timeline-achievements">
                    {exp.achievements.map((achievement, i) => (
                      <li key={i}>{achievement}</li>
                    ))}
                  </ul>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Projects Section */}
      <section id="projects" className="section" ref={el => sections.current.projects = el}>
        <div className="container">
          <h2 className="section-title">Technical Projects</h2>
          <div className="projects-grid">
            {resumeData.projects.map((project, index) => (
              <div key={index} className="project-card" style={{ animationDelay: `${index * 0.1}s` }}>
                <div className="project-header">
                  <h3>{project.name}</h3>
                  <span className="project-tech">{project.technologies}</span>
                </div>
                <p className="project-description">{project.description}</p>
                <ul className="project-achievements">
                  {project.achievements.map((achievement, i) => (
                    <li key={i}>{achievement}</li>
                  ))}
                </ul>
                <div className="project-links">
                  <button className="project-link">View Details</button>
                  <button className="project-link">GitHub</button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Skills Section */}
      <section id="skills" className="section" ref={el => sections.current.skills = el}>
        <div className="container">
          <h2 className="section-title">Technical Skills</h2>
          <div className="skills-container">
            {Object.entries(resumeData.skills).map(([category, items]) => (
              <div key={category} className="skill-category">
                <h3>{category}</h3>
                <div className="skills-grid">
                  {items.map(skill => (
                    <div key={skill} className="skill-item">
                      <span className="skill-name">{skill}</span>
                      <div className="skill-level">
                        <div 
                          className="skill-progress" 
                          style={{ width: `${Math.random() * 30 + 70}%` }}
                        ></div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Education Section */}
      <section id="education" className="section" ref={el => sections.current.education = el}>
        <div className="container">
          <h2 className="section-title">Education</h2>
          <div className="education-grid">
            {resumeData.education.map((edu, index) => (
              <div key={index} className="education-card">
                <h3>{edu.degree}</h3>
                <h4>{edu.institution}</h4>
                <span className="education-period">{edu.period}</span>
                <p>{edu.details}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Certifications Section */}
      <section id="certifications" className="section" ref={el => sections.current.certifications = el}>
        <div className="container">
          <h2 className="section-title">Certifications & Achievements</h2>
          <div className="certifications-grid">
            {resumeData.certifications.map((cert, index) => (
              <div key={index} className="certification-card">
                <h3>{cert.name}</h3>
                <p>{cert.issuer}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Contact Section */}
      <section id="contact" className="section" ref={el => sections.current.contact = el}>
        <div className="container">
          <h2 className="section-title">Get In Touch</h2>
          <div className="contact-grid">
            <div className="contact-info">
              <h3>Let's work together!</h3>
              <p>I'm always interested in new opportunities and exciting projects.</p>
              <div className="contact-details">
                <div className="contact-item">
                  <span>üìß</span>
                  <a href={`mailto:${resumeData.contact.email}`}>{resumeData.contact.email}</a>
                </div>
                <div className="contact-item">
                  <span>üìû</span>
                  <a href={`tel:${resumeData.contact.phone}`}>{resumeData.contact.phone}</a>
                </div>
                <div className="contact-item">
                  <span>üîó</span>
                  <a href={`https://${resumeData.contact.linkedin}`} target="_blank" rel="noopener noreferrer">
                    {resumeData.contact.linkedin}
                  </a>
                </div>
              </div>
            </div>
            <form className="contact-form">
              <input type="text" placeholder="Your Name" required />
              <input type="email" placeholder="Your Email" required />
              <textarea placeholder="Your Message" rows="5" required></textarea>
              <button type="submit" className="btn-primary">Send Message</button>
            </form>
          </div>
        </div>
      </section>

      {/* Footer */}
      <footer className="footer">
        <div className="container">
          <p>&copy; 2024 {resumeData.name}. All rights reserved.</p>
          <p>Built with React & FastAPI</p>
        </div>
      </footer>

      {/* Backend Status */}
      <div className={`status-indicator ${backendStatus}`}>
        Backend: {backendStatus === 'connected' ? '‚úÖ Connected' : '‚ùå Disconnected'}
      </div>
    </div>
  );
}

export default App;
