import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './App.css';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

function App() {
  const [userContext, setUserContext] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [backendStatus, setBackendStatus] = useState('checking');
  const [activeSection, setActiveSection] = useState('home');
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const sections = useRef({
    home: null,
    about: null,
    projects: null,
    skills: null,
    experience: null,
    education: null,
    certifications: null,
    contact: null
  });

  useEffect(() => {
    checkBackendConnection();
    setupIntersectionObserver();
  }, []);

  const setupIntersectionObserver = () => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            setActiveSection(entry.target.id);
          }
        });
      },
      { threshold: 0.5 }
    );

    Object.values(sections.current).forEach(section => {
      if (section) observer.observe(section);
    });
  };

  const checkBackendConnection = async () => {
    try {
      await axios.get(`${API_BASE_URL}/`);
      setBackendStatus('connected');
    } catch (err) {
      setBackendStatus('disconnected');
    }
  };

  const scrollToSection = (sectionId) => {
    sections.current[sectionId]?.scrollIntoView({ behavior: 'smooth' });
    setIsMenuOpen(false);
  };

  const getLocationAndContext = async () => {
    if (backendStatus === 'disconnected') {
      setError('Backend server is not running. Please start the backend server first.');
      return;
    }

    setIsLoading(true);
    setError(null);

    if (!navigator.geolocation) {
      setError("Geolocation is not supported by your browser.");
      setIsLoading(false);
      return;
    }

    navigator.geolocation.getCurrentPosition(
      async (position) => {
        const { latitude, longitude } = position.coords;
        try {
          const response = await axios.post(`${API_BASE_URL}/get-context/`, {
            latitude,
            longitude
          });
          setUserContext(response.data);
          setError(null);
        } catch (err) {
          setError('Failed to get context from server.');
        } finally {
          setIsLoading(false);
        }
      },
      (err) => {
        setError('Please allow location access to experience the full feature.');
        setIsLoading(false);
      }
    );
  };

  // Sample data - replace with your actual information
  const profileData = {
    name: "Yashwanth",
    title: "AI & Machine Learning Engineer",
    bio: "Building intelligent systems that learn and adapt. Passionate about deep learning, computer vision, and creating AI that makes a difference.",
    email: "yashwanth@email.com",
    phone: "+1 (555) 123-4567",
    location: "San Francisco, CA",
    social: {
      github: "https://github.com/yashwanth",
      linkedin: "https://linkedin.com/in/yashwanth",
      twitter: "https://twitter.com/yashwanth",
      kaggle: "https://kaggle.com/yashwanth"
    }
  };

  const projects = [
    {
      id: 1,
      title: "Neural Style Transfer App",
      description: "Real-time style transfer using CNN and TensorFlow",
      technologies: ["Python", "TensorFlow", "CNN", "Flask"],
      github: "https://github.com/yashwanth/style-transfer",
      live: "https://style-transfer-demo.vercel.app",
      image: "https://images.unsplash.com/photo-1620641788421-7a1c342ea42e?w=400&h=300&fit=crop"
    },
    {
      id: 2,
      title: "Predictive Maintenance System",
      description: "ML system predicting equipment failures using sensor data",
      technologies: ["Python", "Scikit-learn", "XGBoost", "IoT"],
      github: "https://github.com/yashwanth/predictive-maintenance",
      live: null,
      image: "https://images.unsplash.com/photo-1581091226033-d5c48150dbaa?w=400&h=300&fit=crop"
    },
    {
      id: 3,
      title: "Sentiment Analysis API",
      description: "Real-time sentiment analysis for social media monitoring",
      technologies: ["Python", "Transformers", "FastAPI", "Docker"],
      github: "https://github.com/yashwanth/sentiment-api",
      live: "https://sentiment-api.example.com",
      image: "https://images.unsplash.com/photo-1533750349088-cd871a92f312?w=400&h=300&fit=crop"
    }
  ];

  const skills = {
    "Machine Learning": ["TensorFlow", "PyTorch", "Scikit-learn", "XGBoost"],
    "Deep Learning": ["CNN", "RNN", "Transformers", "GANs"],
    "Programming": ["Python", "C++", "Java", "JavaScript"],
    "Tools": ["Docker", "Kubernetes", "AWS", "Git"],
    "Data Processing": ["Pandas", "NumPy", "Spark", "Hadoop"]
  };

  const experience = [
    {
      company: "Tech Innovations Inc.",
      position: "Senior AI Engineer",
      period: "2022 - Present",
      description: "Led development of machine learning pipelines for real-time data processing. Improved model accuracy by 35% through advanced feature engineering."
    },
    {
      company: "Data Science Labs",
      position: "ML Researcher",
      period: "2020 - 2022",
      description: "Researched and implemented novel computer vision algorithms. Published 3 papers in top AI conferences."
    }
  ];

  const education = [
    {
      institution: "Stanford University",
      degree: "M.S. in Computer Science",
      period: "2018 - 2020",
      specialization: "Artificial Intelligence"
    },
    {
      institution: "MIT",
      degree: "B.S. in Computer Science",
      period: "2014 - 2018",
      specialization: "Machine Learning"
    }
  ];

  const certifications = [
    {
      name: "AWS Certified Machine Learning Specialist",
      issuer: "Amazon Web Services",
      date: "2023"
    },
    {
      name: "Google Cloud Professional Data Engineer",
      issuer: "Google Cloud",
      date: "2022"
    },
    {
      name: "TensorFlow Developer Certificate",
      issuer: "Google",
      date: "2021"
    }
  ];

  return (
    <div className="App">
      {/* Navigation */}
      <nav className="navbar">
        <div className="nav-container">
          <div className="nav-logo">
            <span>Y</span>ashwanth
          </div>
          <div className={`nav-menu ${isMenuOpen ? 'active' : ''}`}>
            {['home', 'about', 'projects', 'skills', 'experience', 'education', 'certifications', 'contact'].map((item) => (
              <button
                key={item}
                className={`nav-link ${activeSection === item ? 'active' : ''}`}
                onClick={() => scrollToSection(item)}
              >
                {item.charAt(0).toUpperCase() + item.slice(1)}
              </button>
            ))}
          </div>
          <button className="nav-toggle" onClick={() => setIsMenuOpen(!isMenuOpen)}>
            <span></span>
            <span></span>
            <span></span>
          </button>
        </div>
      </nav>

      {/* Hero Section */}
      <section id="home" className="hero" ref={el => sections.current.home = el}>
        <div className="hero-background">
          <div className="particles"></div>
        </div>
        <div className="hero-content">
          <div className="hero-text">
            <h1 className="hero-title">
              Hi, I'm <span className="text-gradient">{profileData.name}</span>
            </h1>
            <h2 className="hero-subtitle">{profileData.title}</h2>
            <p className="hero-description">{profileData.bio}</p>
            <div className="hero-buttons">
              <button className="btn-primary" onClick={() => scrollToSection('projects')}>
                View My Work
              </button>
              <button className="btn-secondary" onClick={() => scrollToSection('contact')}>
                Get In Touch
              </button>
            </div>
          </div>
          <div className="hero-visual">
            <div className="floating-card">
              <div className="ai-animation"></div>
              <button 
                className="context-button"
                onClick={getLocationAndContext}
                disabled={isLoading}
              >
                {isLoading ? '🌍 Analyzing Environment...' : '🎯 Adapt to Environment'}
              </button>
            </div>
          </div>
        </div>
        <div className="scroll-indicator">
          <div className="scroll-arrow"></div>
        </div>
      </section>

      {/* About Section */}
      <section id="about" className="section" ref={el => sections.current.about = el}>
        <div className="container">
          <h2 className="section-title">About Me</h2>
          <div className="about-grid">
            <div className="about-content">
              <p>
                I'm a passionate AI and Machine Learning Engineer with over 5 years of experience 
                in developing intelligent systems. My expertise lies in deep learning, computer vision, 
                and natural language processing.
              </p>
              <p>
                I thrive on solving complex problems and creating AI solutions that have real-world impact. 
                When I'm not coding, you can find me contributing to open-source projects, writing technical blogs, 
                or exploring the latest advancements in AI research.
              </p>
              <div className="about-stats">
                <div className="stat">
                  <h3>50+</h3>
                  <p>Projects Completed</p>
                </div>
                <div className="stat">
                  <h3>5+</h3>
                  <p>Years Experience</p>
                </div>
                <div className="stat">
                  <h3>25+</h3>
                  <p>Technologies Mastered</p>
                </div>
              </div>
            </div>
            <div className="about-image">
              <div className="profile-card">
                <div className="profile-image"></div>
                <div className="profile-info">
                  <h3>{profileData.name}</h3>
                  <p>{profileData.title}</p>
                  <p>📍 {profileData.location}</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Projects Section */}
      <section id="projects" className="section" ref={el => sections.current.projects = el}>
        <div className="container">
          <h2 className="section-title">Featured Projects</h2>
          <div className="projects-grid">
            {projects.map((project, index) => (
              <div key={project.id} className="project-card" style={{ animationDelay: `${index * 0.1}s` }}>
                <div className="project-image">
                  <img src={project.image} alt={project.title} />
                  <div className="project-overlay">
                    <div className="project-links">
                      <a href={project.github} target="_blank" rel="noopener noreferrer" className="project-link">
                        GitHub
                      </a>
                      {project.live && (
                        <a href={project.live} target="_blank" rel="noopener noreferrer" className="project-link">
                          Live Demo
                        </a>
                      )}
                    </div>
                  </div>
                </div>
                <div className="project-content">
                  <h3>{project.title}</h3>
                  <p>{project.description}</p>
                  <div className="project-technologies">
                    {project.technologies.map(tech => (
                      <span key={tech} className="tech-tag">{tech}</span>
                    ))}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Skills Section */}
      <section id="skills" className="section" ref={el => sections.current.skills = el}>
        <div className="container">
          <h2 className="section-title">Technical Skills</h2>
          <div className="skills-grid">
            {Object.entries(skills).map(([category, items]) => (
              <div key={category} className="skill-category">
                <h3>{category}</h3>
                <div className="skill-items">
                  {items.map(skill => (
                    <div key={skill} className="skill-item">
                      <span className="skill-name">{skill}</span>
                      <div className="skill-bar">
                        <div 
                          className="skill-progress" 
                          style={{ width: `${Math.random() * 30 + 70}%` }}
                        ></div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Experience Section */}
      <section id="experience" className="section" ref={el => sections.current.experience = el}>
        <div className="container">
          <h2 className="section-title">Work Experience</h2>
          <div className="timeline">
            {experience.map((exp, index) => (
              <div key={index} className="timeline-item">
                <div className="timeline-content">
                  <h3>{exp.position}</h3>
                  <h4>{exp.company}</h4>
                  <span className="timeline-period">{exp.period}</span>
                  <p>{exp.description}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Education Section */}
      <section id="education" className="section" ref={el => sections.current.education = el}>
        <div className="container">
          <h2 className="section-title">Education</h2>
          <div className="education-grid">
            {education.map((edu, index) => (
              <div key={index} className="education-card">
                <h3>{edu.degree}</h3>
                <h4>{edu.institution}</h4>
                <span className="education-period">{edu.period}</span>
                <p>{edu.specialization}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Certifications Section */}
      <section id="certifications" className="section" ref={el => sections.current.certifications = el}>
        <div className="container">
          <h2 className="section-title">Certifications</h2>
          <div className="certifications-grid">
            {certifications.map((cert, index) => (
              <div key={index} className="certification-card">
                <h3>{cert.name}</h3>
                <p>{cert.issuer}</p>
                <span className="certification-date">{cert.date}</span>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Contact Section */}
      <section id="contact" className="section" ref={el => sections.current.contact = el}>
        <div className="container">
          <h2 className="section-title">Get In Touch</h2>
          <div className="contact-grid">
            <div className="contact-info">
              <h3>Let's work together!</h3>
              <p>I'm always interested in new opportunities and exciting projects.</p>
              <div className="contact-details">
                <div className="contact-item">
                  <span>📧</span>
                  <a href={`mailto:${profileData.email}`}>{profileData.email}</a>
                </div>
                <div className="contact-item">
                  <span>📞</span>
                  <a href={`tel:${profileData.phone}`}>{profileData.phone}</a>
                </div>
                <div className="contact-item">
                  <span>📍</span>
                  <span>{profileData.location}</span>
                </div>
              </div>
              <div className="social-links">
                {Object.entries(profileData.social).map(([platform, url]) => (
                  <a key={platform} href={url} target="_blank" rel="noopener noreferrer" className="social-link">
                    {platform}
                  </a>
                ))}
              </div>
            </div>
            <form className="contact-form">
              <input type="text" placeholder="Your Name" required />
              <input type="email" placeholder="Your Email" required />
              <textarea placeholder="Your Message" rows="5" required></textarea>
              <button type="submit" className="btn-primary">Send Message</button>
            </form>
          </div>
        </div>
      </section>

      {/* Footer */}
      <footer className="footer">
        <div className="container">
          <p>&copy; 2024 {profileData.name}. All rights reserved.</p>
          <p>Built with React & FastAPI</p>
        </div>
      </footer>

      {/* Backend Status */}
      <div className={`status-indicator ${backendStatus}`}>
        Backend: {backendStatus === 'connected' ? '✅ Connected' : '❌ Disconnected'}
      </div>
    </div>
  );
}

export default App;
